<script>
import { TERRITORIES, PARTIES } from '../constants'

export default {
  name: 'SelectorNew',
  data() {
    return {
      territoriesOpen: false,
      partiesOpen: false,
      territories: TERRITORIES.map((group, gi) =>
        group.map((t, i) => ({ name: t.name, selected: false, svgIndex: t.svgIndex }))
      ),
      parties: PARTIES.map((p) => ({ name: p.name, id: p.id, slant: 3 })),
    }
  },
  computed: {
    selectedParties() {
      return this.parties.filter((p) => p.slant !== 3)
    },
    selectedTerritories() {
      return this.territories.flat().filter((t) => t.selected)
    },
  },
  watch: {
    selectedTerritories(values) {
      this.$emit('changeLocations', values)

      const shapes = document.querySelectorAll('.map-container svg > g')

      for (const o of shapes) {
        o.classList.remove('selected')
      }

      for (const v of values) {
        shapes[v.svgIndex]?.classList.add('selected')
      }
    },
    selectedParties(values) {
      // this.$emit('changeParties', values)
      this.$emit('changeParties', this.parties) // just emit all
    },
  },
}
</script>

